mapper.select:
select c.cakeId as cakeId, c.cakeImg as cakeImg, c.cakeName as cakeName, cn.price as price, n.bulk as bulk, n.numberName as numberName, n.sweetness as sweetness, n.tableware as tableware, n.weight as weight, f.flavorId as flavorId, f.flavorName as flavorName FROM cake_number cn left join lecake_cake c on cn.cakeId=c.cakeId left join lecake_number n on cn.numberId= n.numberId left join lecake_flavor f on c.flavorId= f.flavorId where 1=1
and f.flavorId=#{flavorId}
and cn.numberId=#{numberId}
GROUP BY c.cakeId
